/**
 * @fileoverview Errors if autogenerated ember comments are left in files
 * @author Dexter Edwards
 */
"use strict";

//------------------------------------------------------------------------------
// Rule Definition
//------------------------------------------------------------------------------

module.exports = {
    meta: {
        docs: {
            description: "Errors if autogenerated ember comments are left in files",
            category: "Stylistic Issues",
            recommended: true
        },
        fixable: "code",
        schema: [
            // fill in your schema
        ]
    },

    create: function(context) {

        const sourceCode = context.getSourceCode();

        const AUTOGENERATED_COMMENTS = [
            ' Replace this with your real tests.',
            ' Set any properties with this.set(\'myProperty\', \'value\');',
            ' Handle any actions with this.set(\'myAction\', function(val) { ... });',
            ' Template block usage:'
        ]
        
        //----------------------------------------------------------------------
        // Helpers
        //----------------------------------------------------------------------

        //----------------------------------------------------------------------
        // Public
        //----------------------------------------------------------------------

        return {
            Program() {
                const comments = sourceCode.getAllComments();
                for(let comment of comments) {
                    if(AUTOGENERATED_COMMENTS.includes(comment.value)) {
                        context.report({
                            node: null, // Intentionally using loc instead
                            loc: comment.loc,
                            message: 'Delete this autogenerated comment',
                            fix(fixer) {
                                return fixer.removeRange(comment.range);
                            }
                        });
                    }
                }
            }
        };
    }
};